import React,{ Component } from 'react';
import axios from 'axios';
import NavBar from './NavBar';


export default class RestAllDetails extends Component{
  constructor(props){
    super(props);

    this.state={
      Restaurants:[]
    };
  }

  componentDidMount(){
    this.retrieveRestaurant();
  }

  retrieveRestaurant(){
    axios.get("http://localhost:8000/Restaurants").then(res =>{
      if(res.data.success){
        this.setState({
          Restaurants:res.data.existingRestaurant
        });
        console.log(this.state.Restaurants)
      }
    });
  }


    render(){
      return(
        
        <div className="container">
          <NavBar/>
            <h2>All Restaurant Details</h2>
            <table className="table table-hover" style={{marginTop:'40px'}}>
            <thead>
              <tr>
                   <th scope="col">#</th>
                   <th scope="col">Menue</th>
                   <th scope="col">FULL NAME</th>
                   <th scope="col">ADDRESS</th>
                   <th scope="col">MOBILE NUMBER</th>
                   <th scope="col">Date</th>
                   <th scope="col">NO. OF GUESTS</th>
                   <th scope="col">NO. OF CHILDREN</th>
                   <th scope="col">SPECIAL REQUESTS</th>
                   
              </tr>
            </thead>

            <tbody>
                 {this.state.Restaurants.map((Restaurants,index)=>(
                   <tr key={index}>
                     <th scope="row">{index+1}</th>
                     <td>{Restaurants.menue}</td>
                     <td>{Restaurants.name}</td>
                     <td>{Restaurants.address}</td>
                     <td>{Restaurants.mobile}</td>
                     <td>{Restaurants.date}</td>
                     <td>{Restaurants.aguest}</td>
                     <td>{Restaurants.cguest}</td>
                     <td>{Restaurants.notes}</td>
                                        </tr>
                 ))}
               </tbody>


            </table>
  
        </div>
        
        
       
        
      )
      }
  }
